dcode <- function(tex) {
  tmp1 <- c(LETTERS, letters)
  tmp2 <- setdiff(unique(unlist(strsplit(tex, split=""))), tmp1)
  let <- c(tmp1, tmp2)
  names(let) <- c(rev(tmp1), tmp2)
  sapply(strsplit(tex, split=""), function(x){
    paste(let[x], collapse="")
  })
}

